import React, { Component } from 'react';
import { Bar } from 'react-chartjs-2';

export default class ChartUsuarioMes extends Component {
    constructor(props){
        super(props);
        this.state = {
            chartData: ''
        }
    }

    componentDidMount(){
        this.props.users.map(user => {
            if (moment(user.CREATED_AT).isSame(`${mes}`, 'month')){
                usuarios_mes = [ ...usuarios_mes, user.CREATED_AT];
            }
                return 0;
        });
        const data = {
                        labels: ["Janeiro", "Fevereiro", "Março", "Abril", "Maio", "Junho", "Julho"],
                        datasets: [{
                                        label: 'Usuários no Mês',
                                        data: [50, 10, 100, 2, 20, 150, 45],
                                        backgroundColor: [
                                                            'rgb(255, 99, 132)',
                                                            'rgb(50, 10, 100)',
                                                            'rgb(10, 100, 100)',
                                                            'rgb(10, 100, 170)',
                                                            'rgb(120, 25, 170)',
                                                            'rgb(180, 120, 10)',
                                                            'rgb(1, 120, 10)'
                                                        ]
                                    }]
                    }
        this.setState({
            chartData: data
        })
    }

    render(){
        const { chartData } = this.state;
        return (
            <div>
                <Bar
                  data={chartData}
                  options={{ maintainAspectRatio: false }}
                />
            </div>
        ); 
    }
}
