import React, { Component } from 'react';
import { Pie } from 'react-chartjs-2';
import api from '../services/api';
import moment from 'moment';
import { Container } from './styles';

export default class ChartCasamentoMês extends Component {
    constructor(props){
        super(props);
        this.state = {
            chartData: '',
            mes: ''
        }
    }

    async componentDidMount(){
        let users = await api.get("/user").then(resp => resp.data);
        const { mes } = this.state;
        let estilo = new Array(3);
        users.map(user => {
          if (moment(user.CREATED_AT).isSame(`${mes}`, 'month')){
                if (user.STYLE === "classico") {
                    ++estilo[0];
                }else if (user.STYLE === "moderno") {
                    ++estilo[1];
                } else {
                    ++estilo[2];
                }
          }
        })
    }

    handleMes = e => {
        this.setState({
            mes: e.target.value
        })
    }

    render(){
        return (
            <div>
                <Container>
                <input type="month" placeholder="Ex: 2020-10" onChange={this.handleMes} />
                <Pie
                  data={this.state.chartData}
                  options={{ maintainAspectRatio: false }}
                />
                </Container>
            </div>
        ); 
    }
}
