import React, { Component } from 'react';
import { Bar } from 'react-chartjs-2';
import moment from 'moment';

export default class ChartUsuarioMes extends Component {
    constructor(props){
        super(props);
        this.state = {
            chartData: '',
            meses: ["Janeiro", "Fevereiro", "Março", "Abril", "Maio", "Junho", "Julho", "Agosto", "Setembro",
                    "Outubro", "Novembro", "Dezembro"]
        }
    }

    componentDidUpdate(){
        console.log(this.props.users);
    }

    componentDidMount(){  
        let total = 0;
        let mensais = [];
        for (let mes = 1; mes <= 12; mes++) {
            this.props.users.map(user => {
                if (moment(user.CREATED_AT).isSame(`${2020}-${mes}`, 'month')){
                    total++;
                }
                return 0;
            })
            mensais = [ ...mensais, total];
            total = 0;
        }
        mensais = [200, 500, 100, 800, 1000, 350, 120, 247, 987, 254, 147, 356];
        this.setState({
            chartData: {
                         labels: this.state.meses,
                         datasets: [{
                                      label: 'Criados no Mês',
                                      data: mensais,
                                      backgroundColor: ['rgb(255, 99, 132)', 'rgb(50, 10, 100)', 'rgb(10, 100, 100)',
                                                        'rgb(10, 100, 170)', 'rgb(120, 25, 170)', 'rgb(180, 120, 10)',
                                                        'rgb(1, 120, 10)', 'rgb(255, 99, 132)', 'rgb(50, 10, 100)',
                                                        'rgb(10, 100, 100)', 'rgb(10, 100, 2)', 'rgb(120, 25, 170)']
                                   }]
                        }
        })
    }

    render(){
        const { chartData } = this.state;
        return (
            <div>
                <Bar
                  data={chartData}
                  options={{ maintainAspectRatio: false }}
                />
                <input type="button" value="teste" onClick={this.teste} />
            </div>
        ); 
    }
}
