import React, { Component } from 'react';
import { Bar } from 'react-chartjs-2';
import moment from 'moment';

export default class ChartUsuarioMes extends Component {
    constructor(props){
        super(props);
        this.state = {
            chartData: ''
        }
    }

    componentDidMount(){
        let vl_mensais = [];
        let total = 0;
        for (var mes = 1; mes <= 12; mes++) {
            this.props.users.map(user => {
                if (moment(user.CREATED_AT).isSame(`${2020}-${mes}`, 'month')){
                    ++total;
                }
                    return 0;
            });
            vl_mensais = [ ...vl_mensais, total];
            total = 0;
        }
        const data = {
                        labels: ["Janeiro", "Fevereiro", "Março", "Abril", "Maio", "Junho", "Julho"],
                        datasets: [{
                                        label: 'Criados no Mês',
                                        data: vl_mensais,
                                        backgroundColor: [
                                                            'rgb(255, 99, 132)',
                                                            'rgb(50, 10, 100)',
                                                            'rgb(10, 100, 100)',
                                                            'rgb(10, 100, 170)',
                                                            'rgb(120, 25, 170)',
                                                            'rgb(180, 120, 10)',
                                                            'rgb(1, 120, 10)'
                                                        ]
                                    }]
                    }
        this.setState({
            chartData: data
        })
    }

    render(){
        const { chartData } = this.state;
        return (
            <div>
                <Bar
                  data={chartData}
                  width={100}
                  height={50}
                  options={{ maintainAspectRatio: false }}
                />
            </div>
        ); 
    }
}
